exports.Box = function (id, color) {
	this.id = id;
	this.color = color;
	this.power = undefined;
	this.killed = false;

	this.valX = Math.floor(Math.random() * 690)-5;
	this.valY = Math.floor(Math.random() * 495)-5;
	this.vitX = Math.floor(Math.random() * 11);
	this.vitY = Math.floor(Math.random() * 11);

	this.kill = function () {
		this.killed = true;
	}

	this.isKilled = function() {
		return this.killed;
	}

	this.getPower = function() {
		return this.power;
	}

	this.setPower = function(power) {
		this.power = power;
	}

	this.getColor = function() {
		return this.color;
	}
}