exports.Player = function (socket) {
	this.infos = {};
	this.socket = socket;
	this.points = 0;
	this.color = undefined;
	this.ready = false;

	this.setInfos = function(infos) {
		this.infos = infos;
	}

	this.getInfos = function () {
		return this.infos;
	}

	this.setColor = function(color) {
		this.color = color;
	}

	this.getColor = function () {
		return this.color;
	}

	this.setReady = function (ready) {
		this.ready = ready;
	}

	this.getReady = function () {
		return this.ready;
	}
}