exports.Player = function (socket) {
	this.infos = {};
	this.socket = socket;
	this.points = 0;
	this.color = undefined;
	this.ready = false;

	this.setInfos = function(infos) {
		this.infos = infos;
	}

	this.getInfos = function () {
		return this.infos;
	}

	this.setColor = function(color) {
		this.color = color;
	}

	this.getColor = function () {
		return this.color;
	}

	this.setReady = function (ready) {
		this.ready = ready;
	}

	this.getReady = function () {
		return this.ready;
	}

	this.addPoints = function(apts) {
		this.points = this.points+apts;
	}

	this.rmPoints = function(rpts) {
		this.points = this.points-rpts;
	}

	this.setPoints = function(points) {
		this.points = points;
	}

	this.getPoints = function() {
		return this.points;
	}
}